import React,{useState, useEffect,Fragment} from 'react';
import Movies  from '../Movies/Movies';
import Movie  from '../Movie/Movie';
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import './App.css';
export const ArtContext = React.createContext();
const App = () =>{
  const [videos,setVideos] = useState([]);
  useEffect(() =>{
    const getVideos = async () =>{
      const request = await fetch('https://orangevalleycaa.org/api/videos');
      const response = await request.json();
      setVideos(response);
    }
    getVideos();
  },[])
  const deleteArt = (e,givenId) =>{
    e.preventDefault();
    let tempArts = videos.filter( art => art.id!==givenId);
    setVideos(tempArts);
  }
  return(
    <Fragment>
    <Router>
      <Switch>
        <ArtContext.Provider value = {deleteArt}>
          <Route exact path = '/' component = {<Movies videos = {videos}/>}/>
        </ArtContext.Provider>
        <Route path='/art/:ardId'render = {(props) => <Movie/>}/>
      </Switch>
    </Router>
    </Fragment>
  )
}

export default App;
