import React,{useState, useEffect,useReducer,Fragment} from 'react';
const initialState = 0;
const reducer = (state,action) =>{
  switch (action) {
    case (action==='increase'):
      return state + 1
    case (action==='decrease'):
      return state - 1
    case (action==='reset'):
      return initialState
    default:
      return action

  }
}
const Practice = () =>{
const [count, setCount] = useReducer(reducer,initialState)

  return(
    <Fragment>
      <input type = 'sumbit'  onClick = {() => setCount('increase')}/>
      <input type = 'sumbit'  onClick = {() => setCount('decrease')}/>
      <input type = 'sumbit'  onClick = {() => setCount('reset')}/>
      <h1>Count: {count}</h1>
    </Fragment>
  )
}

export default Practice;
