import React from 'react';
const Book = (props) => {
  const {title,author,pages,updateBookProperties} = props;
  return (
      <>
       <h2 contentEditable suppressContentEditableWarning onBlur = {(event) => updateBookProperties()}>{title}</h2>
       <p  contentEditable suppressContentEditableWarning onBlur = {(event) => updateBookProperties()}>{author}</p>
       <p  contentEditable suppressContentEditableWarning onBlur = {(event) => updateBookProperties()}>{pages}</p>
      </>
  )
}
export default Book;
