import React from 'react';
const Book = (props) => {
  const {bookId,title,author,pages,updateInfo} = props;
  return (
      <>
       <h2 contentEditable onBlur = {(e) => updateInfo(title,e.target.innerText,bookId)}>{title}</h2>
       <p contentEditable onBlur = {(e) => updateInfo(author,e.target.innerText,bookId)}>{author}</p>
       <p contentEditable onBlur = {(e) => updateInfo(pages,e.target.innerText,bookId)}>{pages}</p>
      </>
  )
}
export default Book;
