import React from 'react';
import Book from './Book.js';
import uuid from 'react-uuid'
import './css/Library.css';
import {GoTrashcan} from 'react-icons/go';
import { BiDotsHorizontalRounded } from 'react-icons/bi';
const Library = ({books,deleteBook,updateInfo}) =>{
  console.log("list books: ",books)
  return(
        <div className="output">
          <ul>
             {
               books.map( book => <li key = {book.bookId}>
                 <Book bookId = {uuid()} title = {book.title} author = {book.author} pages = {book.pages} updateInfo = {updateInfo}/>
                 <span onClick = { ()=> deleteBook(book)}><GoTrashcan/></span>
                 </li>)
             }
          </ul>
      </div>
        )
}
export default Library;
