import React,{ useState, useEffect, useContext,Fragment } from 'react';
import { AiOutlineArrowRight, AiOutlineArrowDown } from 'react-icons/ai';
import './MovieDetail.css';

const MovieDetail = ({art}) => {
  const [ showDescription, setShowDescription ] = useState(true);
  const {video_url, name, description,popularity,image,created_by,duration} = art;
  return(
    <Fragment>
        <section className = 'movieDetail'>
          <h1 style = {{fontFamily:"Merienda One"}}>{name}</h1>
          <video width = {448} height = {200} controls src = {video_url}/>
        </section>
        <aside className = 'list-properies'>
          <div><span className = 'imageContainer'><AiOutlineArrowRight/>Image</span><img src={image} className = 'image'/></div>
          <div><span className = 'authorContainer'><AiOutlineArrowRight/>Title</span><div className = 'author'>{name}</div></div>
          <div><span className = 'created_byContainer'><AiOutlineArrowRight/>Author</span><div className = 'created_by'>{created_by}</div></div>
          <div><span className = 'durationContainer'><AiOutlineArrowRight/>Time</span><div className = 'duration'>{duration}</div></div>
        </aside>
    </Fragment>
  )
}
export default MovieDetail;
