import React,{ useState, useEffect, useContext,Fragment } from 'react';
import { AiOutlineArrowRight, AiOutlineArrowDown } from 'react-icons/ai';
import './MovieDetail.css';

const MovieDetail = ({art}) => {
  const [ showDescription, setShowDescription ] = useState(false);
  const {video_url, name, description} = art;
  return(
    <Fragment>
      <section className = 'movieDetail'>
        <h1>{name}</h1>
        <video width = {448} height = {252} controls src = {video_url}/>
        <div>
        {
          showDescription ? <AiOutlineArrowDown onClick = {()=> setShowDescription(true)} style = {{fontSize:"25px"}}/>`${description}`:<AiOutlineArrowRight onClick = {()=> setShowDescription(false)} style = {{fontSize:"25px"}}/>
        }
        </div>
      </section>
    </Fragment>
  )
}
export default MovieDetail;
