import React,{useState,useEffect,Fragment} from 'react';

const Search = ({books,deleteBook}) =>{
  const [input, setInput] = useState('');
  let searchBooks = books.map( book => book.title.toLowerCase().includes(input.toLowerCase()));
  return(
    <Fragment>
      <input type = 'search' placeholder = 'Search' value = {input} onChange = {(e) => setInput(e.target.value)}/>
      <Library books = {searchBooks} deleteBook = {deleteBook}/>
    </Fragment>
  )
}
