import React,{useState, useEffect,useRef} from 'react';


const App = () =>{
  const [message,setMessage] = useState({
    firstName:'',
    lastName:''
  });

  useEffect(()=>{
      console.log(`first name: ${message.firstName}`);
  },[message.firstName])
  useEffect(()=>{
      console.log(`last name: ${message.lastName}`);
  },[message.lastName])
  const showMessage = (e) =>{
    e.preventDefault();
    console.log(message.firstName,message.lastName);
  }
  return(
    <>
      <form  onSubmit = {(e) => showMessage(e)}>
        <input  type = 'text' value = {message.firstName} onChange = {(e) => setMessage( {...message, firstName: e.target.value})}/>
        <input  type = 'text' value = {message.lastName} onChange = {(e) => setMessage({...message, lastName: e.target.value})}/>
        <input  type = 'submit' value = 'Submit' name = 'Submit'/>
      </form>
      <h1>First Name:{message.firstName}</h1>
      <h1>Last Name:{message.lastName}</h1>
    </>
  )
}
export default App;


