import React from 'react';
import AddBook from './AddBook.js';
import './css/App.css';
import Library from './Library';
import {without} from 'lodash';
class App extends React.Component {
  constructor(){
    super();
    this.state = {
      bookList : [],

    }
  }
  componentDidMount(){
    fetch('./bookList.json')
    .then(data=> data.json())
    .then(data=> this.setState({bookList:data}));
  }

  deleteBook = (book) => {
    let tempList = without(this.state.bookList, book);
    this.setState({bookList: tempList})
  }
  render(){
    const {bookList} = this.state;
    return(
      <div>
        <Library bookList={bookList}/>
      </div>
    )
  }
}
export default App;
