import React,{useState,useEffect,Fragment} from 'react';
import Library from '../Library/Library.js';
import '../css/App.css';
const Search = ({books,deleteBook}) =>{
  const [input, setInput] = useState('');
  const [searchBy, setSearchBy] = useState('title');
  let searchBooks = books.filter( book => (searchBy!=='pages') ? book[searchBy].toLowerCase().includes(input.toLowerCase()) : book[searchBy].includes(input));
  return(
    <Fragment>
      <input type = 'search' placeholder = 'Search' value = {input} onChange = {(e) => setInput(e.target.value)}/>
      <select onChange = {(e) => setSearchBy(e.target.value)}>
        <option value = 'title'>title</option>
        <option value = 'author'>author</option>
        <option value = 'pages'>pages</option>
      </select>
      <Library books = {searchBooks} deleteBook = {deleteBook}/>
    </Fragment>
  )
}
export default Search;
