import React,{useState,useEffect,Fragment} from 'react';
import './css/App.css';
import uuid from 'react-uuid';


const App = () =>{
  const [books,setBooks] = useState([]);
  useEffect( () =>{
    fetch('./bookList.json')
    .then( data => data.json())
    .then( data => {
      data.map( book => book.bookId = uuid());
      setBooks(data);
    });
    console.log(books);
  },[]);

  return(
    <Fragment>
     <h1>Hello book</h1>
    </Fragment>
  )

  }

export default App;
