import React,{useState,useEffect,Fragment,Component} from 'react';
import { Router, Route, Switch } from "react-router";
import Library from './Library/Library.js';
import Search from './Search/Search.js';
import DetailBook from './DetailBook/DetailBook.js';
import './css/App.css';
import uuid from 'react-uuid';
import { without } from 'lodash';


class App extends Component{
  constructor(){
    super();
    this.state = {
      books:[]
    }
  }
componentDidMount(){
  fetch('./bookList.json')
  .then( data => data.json())
  .then( data => {
    data.map( book => book.bookId = uuid());
    this.setState({books:data});
  });
}
deleteBook = (book) =>{
  const {books} = this.state;
    let tempBooks = without(books,book);
    this.setState({books:tempBooks});
  }
  render(){
    const {books} = this.state;
    return(
      <Fragment>
      <Router>
           <div className = 'main'>
              <header className = 'header'>
              </header>
                <div className = 'left'></div>
                <div className = 'center'>
                  <Search books = {books} deleteBook = {this.deleteBook}/>
                </div>
                <div className = 'right'></div>
              <footer className = 'footer'></footer>
           </div>
          <Switch>
           <Route exact path='/book/:bookId' render = {(props) =>{
             console.log(props)
           }}/>
         </Switch>
       </Router>
      </Fragment>
    )
  }


  }

export default App;
