import React,{useState,useEffect,Fragment} from 'react';
import uuid from 'react-uuid';
const AddChapter = ({books,bookId}) =>{
  const [chapterTitle, setChapterTitle] = useState('');
  const [pages, setPages] = useState(0);
  const onSubmit = (event) =>{
    event.preventDefault();
    const chapter = {
      chapterId:uuid(),
      chapterTitle:chapterTitle,
      pages: pages
    }
  }
  console.log("Add chapter: ",bookId);
  return(
    <Fragment>
      <form onSubmit = {(e) => onSubmit(e)}>
        <input type = 'text' placeholder = 'Chapter title' value = {chapterTitle} onChange = {(e) => setChapterTitle(e.target.value)} />
        <input type = 'number' placeholder = 'Pages' value = {pages} onChange = {(e) => setPages(e.target.value)}/>
        <input type = 'sumbit' value = 'Submit' name = 'submit'/>
      </form>
    </Fragment>
  )
}
export default AddChapter;
