import React,{ useState, useEffect, Fragment}  from 'react';
import { IoIosArrowForward, IoIosArrowDown} from 'react-icons/io';
import './ListProperties.css'
const ListProperties = ({image, name, created_by, description }) => {
const [arrows, setArrows ] = useState({
  imageToggle:false,
  titleToggle:false,
  authorToggle:false,
  descriptionToggle:false
});
  return(
    <Fragment>
      <ul className = 'list-properies'>
        <li><span className = 'imageContainer'>{arrow ? <IoIosArrowForward className = 'arrow' onClick = {() => setArrow({...arrows, imageToggle:!arrows.imageToggle})}/>:<IoIosArrowDown className = 'arrow' onClick = {() => setArrow(!arrow)}/>}<h3>Image</h3></span></li>
        <li><span className = 'authorContainer'>{arrow ? <IoIosArrowForward className = 'arrow'/>:<IoIosArrowDown className = 'arrow'/>}<h3>Title</h3></span></li>
        <li><span className = 'created_byContainer'>{arrow ? <IoIosArrowForward className = 'arrow'/>:<IoIosArrowDown className = 'arrow'/>}<h3>Author</h3></span></li>
        <li><span className = 'durationContainer'>{arrow ? <IoIosArrowForward className = 'arrow'/>:<IoIosArrowDown className = 'arrow'/>}<h3>Description</h3></span></li>
      </ul>
   </Fragment>
  )
}
export default ListProperties;
