import React,{useState, useEffect,Fragment} from 'react';


const searchStyle = {
  widht:"400px",
  height:"50",
  margin:"0px",
  padding:"0px"
}
const Search = ({searchTerm,onSearch}) => {
  return(
    <Fragment>
      <input type = 'search' placeholder ='Search' className = 'search' value = {searchTerm} style = {searchStyle} onChange = {onSearch}/>
    </Fragment>
  )
}
export default Search;
