import React from 'react';
import './css/App.css';
import Library from './Library';
import {without} from 'lodash';
class App extends React.Component {
  constructor(){
    super();
    this.state = {
      bookList : [],

    }
  }
  componentDidMount(){
    fetch('./bookList.json')
    .then(data=> data.json())
    .then(data=> this.setState({bookList:data}));
  }

  deleteBook = (book) => {
    let tempList = without(this.state.bookList, book);
    this.setState({bookList: tempList})
  }
  updateInfo = (key,value,id) => {
    let tempBooks  = getBookList;
    let findCurrentBookIndex = findIndex(tempBooks,{bookId: id});
    tempBooks[findCurrentBookIndex][key] = value;
    setBookList(tempBooks);
  }
  render(){
    const {bookList} = this.state;
    return(
      <div>
        <Library books={bookList} deleteBook = {this.deleteBook} updateInfo = {this.updateInfo}/>
      </div>
    )
  }
}
export default App;
