import React from 'react';
import './css/App.css';
import Library from './Library';
import uuid from 'react-uuid';
import {without,findIndex} from 'lodash';
class App extends React.Component {
  constructor(){
    super();
    this.state = {
      bookList : []
    }
  }
  componentDidMount(){
    fetch('./bookList.json')
    .then(data=> data.json())
    .then(data=> {
      const books = data.map( book =>{
        book.id = uuid();
        return book;
      })
      this.setState({bookList:books});
    })

  }



  deleteBook = (book) => {
    let tempList = without(this.state.bookList, book);
    this.setState({bookList: tempList})
  }
  updateInfo = (key,value,id) => {
    let tempBooks  = this.state.bookList;
    let findCurrentBookIndex = findIndex(tempBooks,{id: id});
    tempBooks[findCurrentBookIndex][key] = value;
    this.setState({bookList:tempBooks});
  }
  render(){
    const {bookList} = this.state;
    return(
      <div>
        <Library books={bookList} deleteBook = {this.deleteBook} updateInfo = {this.updateInfo}/>
      </div>
    )
  }
}
export default App;
