import React,{useState, useEffect} from 'react';


const App = () =>{
  const [books, setBooks] = useState([]);
  useEffect( () =>{
    const fetchData = async()=>{
      const data = await fetch('./bookList.json').then( books => books.json());
      setBooks(data);
      }
    fetchData();
  },[])
  return(
    <>
      <ul>
        {
          books.map( book => <li><h1>{book.title}</h1></li>)
        }
      </ul>
    </>
  )
}

export default App;
